import React, { useState, useEffect } from "react";
import List from "./List";
import Alert from "./Alert";

function App() {
	const [list, setList] = useState("");
	const [items, setItems] = useState([]);
	const [alert, setAlert] = useState({ show: false, msg: "", type: "" });
	const [isEditing, setIsEditing] = useState(false);
	const [editIndex, setEditIndex] = useState(null);

	useEffect(() => {
		const data = localStorage.getItem("list");
		if (data) {
			setList(data);
		}
	}, []);

	const clearItems = () => {
		setItems([]);
	};

	const handleSubmit = (e) => {
		try {
			e.preventDefault();
			if (!list) {
				// display alert if list is empty

				showAlert(true, "please enter a value", "alert alert-danger");
			} else if (list && isEditing) {
				// deal with editing list

				const newItems = [...items];
				newItems[editIndex] = { id: editIndex, title: list };
				setItems(newItems);
				setIsEditing(false);
			} else {
				// show alert if list is not empty
				showAlert(true, "Item Added To The List", "alert alert-success");

				const newItem = { id: new Date().getTime().toString(), title: list };
				setItems([...items, newItem]);
				setList("");
				localStorage.setItem("list", list);
			}
		} catch (error) {
			console.log(error);
		}
	};

	const showAlert = (show, msg, type) => {
		setAlert({ show, msg, type });
	};

	return (
		<section className="section-center">
			<form onSubmit={handleSubmit} className="grocery-form">
				{alert.show && <Alert {...alert} removeAlert={showAlert} />}
				<h3>grocery bud</h3>
				<div className="form-control">
					<input
						type="text"
						className="grocery"
						placeholder="e.g. eggs"
						value={list}
						onChange={(e) => setList(e.target.value)}
					/>
					<button type="submit" className="submit-btn">
						{isEditing ? "update" : "submit"}
					</button>
				</div>
			</form>
			{items.length > 0 && (
				<div className="grocery-container">
					<List items={items} setItems={setItems} {...isEditing} />
					<button className="clear-btn" onClick={clearItems}>
						clear items
					</button>
				</div>
			)}
		</section>
	);
}

export default App;
