const reducer = (state, action) => {
	if (action.type === "CLEAR_CART") {
		return { ...state, cart: [] };
	}
	if (action.type === "REMOVE") {
		console.log(state, action.id);
		// return { ...state, cart:  };
	}
	return state;
};

export default reducer;
