const reducer = (state, action) => {
	if (action.type === "CLEAR_CART") {
		return { ...state, cart: [] };
	}
	if (action.type === "REMOVE") {
		console.log(state);
		return { ...state, cart: cart.length - cart.id };
	}
	return state;
};

export default reducer;
