import React, { useState, useEffect } from "react";
import { FaSearch } from "react-icons/fa";
import Photo from "./Photo";
const clientID = `?client_id=${process.env.REACT_APP_ACCESS_KEY}`;
const mainUrl = `https://api.unsplash.com/photos/`;
const searchUrl = `https://api.unsplash.com/search/photos/`;

function App() {
	const [loading, setLoading] = useState(true);
	const [pics, setPics] = useState([]);

	const getPics = async () => {
		setLoading(true);
		let url;
		url = `${mainUrl}${clientID}`;
		const response = await fetch(url);
		const data = await response.json();
		setPics(data);
		setLoading(false);
	};

	useEffect(() => {
		getPics();
	}, []);

	const handleSubmit = (e) => {
		e.preventDefault();
		console.log("hihihi");
	};

	return (
		<main>
			<section className="search">
				<form className="search-form">
					<input type="text" className="form-input" placeholder="search" />
					<button type="submit" className="submit-btn" onClick={handleSubmit}>
						<FaSearch />
					</button>
				</form>
			</section>
			<section className="photos">
				<div className="photos-center">
					{loading
						? loading
						: pics.map((pic) => {
								console.log(pic);
								return (
									<article key={pic.id} className="photo">
										<img src={pic.urls.small} alt="" />
										<div className="photo-info">
											<div>
												<h4>{`${pic.user.first_name} ${pic.user.last_name}`}</h4>
												<p>{pic.likes}</p>
											</div>
											<a href={pic.user.links.self}>
												<img
													className="user-img"
													src={pic.user.profile_image.small}
													alt={`${pic.user.first_name} ${pic.user.last_name}`}
												/>
											</a>
										</div>
									</article>
								);
						  })}
				</div>
			</section>
		</main>
	);
}

export default App;
