import React, { useState, useEffect } from "react";
import { FaSearch } from "react-icons/fa";
import Photo from "./Photo";
const clientID = `?client_id=${process.env.REACT_APP_ACCESS_KEY}`;
const mainUrl = `https://api.unsplash.com/photos/`;
const searchUrl = `https://api.unsplash.com/search/photos/`;

function App() {
	const [loading, setLoading] = useState(true);
	const [pics, setPics] = useState([]);
	const [value, setValue] = useState("");

	const getPics = async () => {
		let url;
		url = `${mainUrl}${clientID}`;
		const response = await fetch(url);
		const data = await response.json();
		setPics(data);
		setLoading(false);
	};

	useEffect(() => {
		getPics();
	}, []);

	const handleSubmit = (e) => {
		e.preventDefault();

		const searchPics = async () => {
			let url;
			url = `${searchUrl}${clientID}?page=2&query=${value}`;
			console.log(url);
			const response = await fetch(url);
			const data = await response.json();
			console.log(data, "search");
			setPics(data);
			setLoading(false);
		};
		searchPics();
	};

	return (
		<main>
			<section className="search">
				<form className="search-form">
					<input
						type="text"
						value={value}
						className="form-input"
						placeholder="search"
						onChange={(e) => setValue(e.target.value)}
					/>
					<button type="submit" className="submit-btn" onClick={handleSubmit}>
						<FaSearch />
					</button>
				</form>
			</section>
			{loading ? (
				<h2 className="loading">Loading...</h2>
			) : (
				<section className="photos">
					<div className="photos-center">
						{pics.map((pic) => {
							console.log(pic);
							return (
								<article key={pic.id} className="photo">
									<img src={pic.urls.small} alt="" />
									<div className="photo-info">
										<div>
											<h4>{`${pic.user.first_name} ${pic.user.last_name}`}</h4>
											<p>{pic.likes}</p>
										</div>
										<a href={pic.user.links.self}>
											<img
												className="user-img"
												src={pic.user.profile_image.small}
												alt={`${pic.user.first_name} ${pic.user.last_name}`}
											/>
										</a>
									</div>
								</article>
							);
						})}
					</div>
				</section>
			)}
		</main>
	);
}

export default App;
