const mainUrl = `https://api.unsplash.com/photos/`;
const searchUrl = `https://api.unsplash.com/search/photos/`;

function App() {
	const [loading, setLoading] = useState(true);
	const getPics = async () => {
		const response = await fetch(mainUrl);
		const data = await response.json();
		setLoading(false);
	};
