		<main>
			<section className="search">
				<form className="search-form">
					<input type="text" className="form-input" placeholder="search" />
					<button className="submit-btn">
						<FaSearch />
					</button>
				</form>
			</section>
			<section className="photos">
				<div className="photos-center"></div>
			</section>
		</main>
	);
}

export default App;
